package baseball.domain;public class BaseBallGame {    private final BaseBallNumbers randomBaseBallNumbers;    private BaseBallGame(BaseBallNumbers randomBaseBallNumbers) {        this.randomBaseBallNumbers = randomBaseBallNumbers;    }    public static BaseBallGame initializeGame(BaseBallNumbers randomBaseBallNumbers) {        return new BaseBallGame(randomBaseBallNumbers);    }    public BaseBallGameResult startGame(BaseBallNumbers playerBaseBallNumbers) {        Strike strike = randomBaseBallNumbers.countStrike(playerBaseBallNumbers);        Ball ball = randomBaseBallNumbers.countBall(playerBaseBallNumbers, strike);        return new BaseBallGameResult(strike, ball);    }}